import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "./images/Screenshot (88).png";
import gif from "./images/gif.webm";


function Nav() {

  // login modal
  const navigate = useNavigate();
  const [showLogin, setShowLogin] = useState(false);
  const [getFullName, setGetFullName] = useState("");
  const [getPhone, setGetPhone] = useState("");
  const [getEmail, setGetEmail] = useState("");
  const [getUserName, setGetUserName] = useState("");
  const [getPassword, setGetPassword] = useState("");
  const [getConfirmPassword, setGetConfirmPassword] = useState("");


  const [showModal, setShowModal] = useState(0);

  const showloginHandler = () => {
    setShowLogin(true);
    setShowModal((prevState) => prevState + 1);
  };

  const nextModalHandler = () => {
    setShowModal((prevState) => prevState + 1);
  };
  const closeloginHandler = () => {
    setShowModal((prevState) => prevState - 1);
  };

  const submitHandler = () => {

    let inputs = {
      fullName: getFullName,
      phone: getPhone,
      email: getEmail,
      userName: getUserName,
      password: getPassword,
    };


    console.log(inputs);


  //  const response = 
   fetch("http://localhost:4001/api/create-user",{
      method: "POST",
      headers: {"Content-Type":"application/json",
    },
      body: JSON.stringify(inputs),
    })
    // .then((resp) => resp.json())
    .then((data)=>{

      console.log(data);
      if (data.status === 200) {
        navigate("/Sign")
        alert("Welcome to monkblr Blog" + getFullName);
      }else{
        navigate("/")
        alert("Sign-In Unsuccessful!")
      }

    }).catch((err)=>{
      throw new Error
    })

    setShowModal(0);
    setGetFullName("");
    setGetPhone("");
    setGetEmail("");
    setGetUserName("");
    setGetPassword("");
    setGetConfirmPassword("");
  };


  let show;

  if (showModal === 1) {
    show = (
      <>
        <div>
          <div className="loginForm">
            <button
              style={{
                paddingLeft: "20px",
                color: "grey",
                fontSize: "25px",
                backgroundColor: "rgb(94, 57, 243)",
                border: "none",
              }}
              onClick={closeloginHandler}
            >
              ⬅
            </button>

            <div className="modal1">
              <img src={gif} alt="" />
              <h1>monkblr</h1> <br />
              <h4>
                Welcome to your corner of the internet. You'll nevr be bored
                again
              </h4>
              <h5>Sign up or log in:</h5>
              <button className="modalBtn">Continue with Google</button> <br />
              <button className="modalBtn">Continue with Apple</button> <br />
              <button className="modalBtn" onClick={nextModalHandler}>
                Continue with email
              </button>
              <h4 style={{ color: "rgb(232, 205, 56)" }}>
                Coming from Twitter? Sign up
              </h4>
            </div>
          </div>
        </div>
      </>
    );
  } else if (showModal === 2) {
    show = (
      <>
        <div>
          <div className="loginForm">
            <button
              style={{
                paddingLeft: "20px",
                color: "grey",
                fontSize: "25px",
                backgroundColor: "rgb(94, 57, 243)",
                border: "none",
              }}
              onClick={closeloginHandler}
            >
              ⬅
            </button>
            <div className="modal1">
              <img src={gif} alt="" />
              <h1>monkblr</h1> <br />
              <h4>Enter your Email to log in or register:</h4>
              <input
                className="inp-email"
                type="text"
                placeholder=" Enter Email"
                value={getEmail}
                onChange={(e) => setGetEmail(e.target.value)}
              />{" "}
              <br />

              <button className="nxtBtn" onClick={nextModalHandler}>
                Next ➡
              </button>{" "}
              <br />
            </div>
          </div>
        </div>
      </>
    );
  } else if (showModal === 3) {
    show = (
      <>
        <div>
          <div className="loginForm">
            <button
              style={{
                paddingLeft: "20px",
                color: "grey",
                fontSize: "25px",
                backgroundColor: "rgb(94, 57, 243)",
                border: "none",
              }}
              onClick={closeloginHandler}
            >
              ⬅
            </button>
            <div className="modal1">
              <img src={gif} alt="" />
              <h1>monkblr</h1> <br />
              <h4>
                Welcome to your corner of the internet. <br /> Glad you're here.
              </h4>
              <input
                className=""
                type="text"
                placeholder=" Enter Full Name"
